declare const _default: (theme: any) => {
    root: {
        borderRadius: number;
        padding: number;
        border: string;
        width: string;
    };
    content: {
        fontSize: number;
        fontFamily: any;
        fontWeight: any;
    };
    disabled: {
        backgroundColor: any;
    };
    primary: {
        color: any;
        backgroundColor: any;
        '&:hover': {
            backgroundColor: any;
        };
        '&:active': {
            backgroundColor: any;
        };
    };
    secondary: {
        color: any;
        border: string;
        backgroundColor: any;
        '&:hover': {
            backgroundColor: any;
        };
        '&:active': {
            backgroundColor: any;
        };
    };
};
export default _default;
