declare const _default: (theme: any) => {
    root: {
        borderRadius: number;
        padding: number;
        border: string;
        width: string;
        fontSize: number;
        fontFamily: any;
        fontWeight: any;
        userSelect: string;
        cursor: string;
    };
    disabled: {
        color: any;
        borderColor: any;
    };
    /** Color variants */
    primary: {
        color: any;
        backgroundColor: any;
        '&:hover': {
            backgroundColor: any;
        };
        '&:active': {
            backgroundColor: any;
        };
    };
    secondary: {
        color: any;
        border: string;
        backgroundColor: any;
        '&:hover': {
            backgroundColor: any;
        };
        '&:active': {
            backgroundColor: any;
        };
    };
    /** Sizes */
    normal: {};
    mini: {
        padding: number;
        fontSize: number;
        fontWeight: any;
        borderRadius: number;
    };
};
export default _default;
