declare const _default: (theme: any) => {
    root: {
        border: string;
        display: string;
        borderRadius: number;
        position: string;
    };
    button: {
        display: string;
        width: number;
        border: number;
        height: number;
        padding: number;
        textOverflow: string;
        overflow: string;
        whiteSpace: string;
        backgroundColor: string;
        position: string;
        zIndex: number;
    };
    active: {
        color: string;
        transition: string;
        '&:hover': {
            backgroundColor: string;
        };
    };
    background: {
        backgroundColor: any;
        width: number;
        height: number;
        position: string;
        zIndex: number;
        borderRadius: number;
        transition: string;
    };
};
export default _default;
