declare const _default: (theme: any) => {
    root: {};
    append: {
        display: string;
        alignItems: string;
        justifyContent: string;
        width: number;
    };
    icon: {
        width: number;
        transition: string;
        fill: any;
        '&:hover': {
            fill: any;
        };
    };
};
export default _default;
