declare const _default: (theme: any) => {
    root: {
        '&.withBorder': {
            border: any;
            borderRadius: number;
            boxShadow: any;
        };
    };
    table: {
        borderSpacing: number;
        fontSize: number;
        textAlign: string;
        width: string;
    };
    header: {
        display: string;
        height: number;
    };
    headerLabel: {
        paddingLeft: number;
        paddingTop: number;
        fontSize: number;
        color: any;
        whiteSpace: string;
    };
    headerComponent: {
        width: string;
        display: string;
        justifyContent: string;
        alignItems: string;
    };
};
export default _default;
