declare const _default: (theme: any) => {
    wrapper: {
        borderRadius: number;
        backgroundColor: any;
        display: string;
        flexDirection: string;
        alignItems: string;
        justifyContent: string;
        marginRight: number;
        height: number;
    };
    content: {
        color: any;
        flex: number;
        textAlign: string;
        display: string;
        alignItems: string;
        justifyContent: string;
        padding: number;
        whiteSpace: string;
        fontFamily: any;
        fontWeight: any;
        fontSize: number;
    };
    closeSection: {
        pointerEvents: string;
        color: any;
        height: string;
        width: number;
        backgroundColor: any;
        borderRadius: string;
        display: string;
        alignItems: string;
        justifyContent: string;
        '&:hover': {
            backgroundColor: any;
        };
        '&:active': {
            backgroundColor: any;
        };
    };
    closeIcon: {
        width: number;
        fill: string;
    };
};
export default _default;
