declare const _default: (theme: any) => {
    '@global': {
        '@import': any[];
        html: {
            height: string;
            width: string;
        };
        body: {
            fontFamily: any;
            fontWeight: any;
            height: string;
            width: string;
            padding: number;
            margin: number;
            backgroundColor: any;
            color: any;
        };
        h1: {
            fontSize: number;
            fontFamily: any;
            fontWeight: any;
        };
        h2: {
            fontSize: number;
            fontFamily: any;
            fontWeight: any;
        };
        h3: {
            fontSize: number;
            fontFamily: any;
            fontWeight: any;
        };
        h4: {
            fontSize: number;
            fontFamily: any;
            fontWeight: any;
        };
        p: {
            fontSize: number;
            fontFamily: any;
            fontWeight: any;
        };
        b: {
            fontFamily: any;
            fontWeight: any;
        };
        hr: {
            border: string;
        };
        '#root': {
            height: string;
        };
        'button:focus': {
            outline: string;
        };
        '*, ::after, ::before': {
            boxSizing: string;
        };
        a: {
            color: any;
            textDecoration: string;
            '&:hover': {
                textDecoration: string;
            };
        };
    };
};
export default _default;
